import { Button, Form, Input, Upload, Image } from "antd";
import { useEffect, useState } from "react";
import { UploadOutlined } from "@ant-design/icons";
import { useAppDispatch, useAppSelector } from "../app/hooks";
import {
  loadUserAsync,
  selectUser,
  setAddress,
  setFirstName,
  setLastName,
  setPhone,
  setUserCity,
  updateUserThunk,
} from "../app/slice/userSlice";

const Cabinet = () => {
  const dispatch = useAppDispatch();
  const state = useAppSelector(selectUser);
  const [file, chooseFile] = useState(new File([], ""));
  const [isFileRemoved, removeFile] = useState(true);
  useEffect(() => {
    if (!state.firstName) {
      dispatch(loadUserAsync());
    }
  }, [dispatch, state.firstName]);
  return (
    <>
      <Image
        width={200}
        src={state.image}
        fallback="data:image/png;base64,"
      />
      <Form
        layout={"vertical"}
        onFinish={() => {
          if (!isFileRemoved) dispatch(updateUserThunk(file));
          else dispatch(updateUserThunk(null));
        }}
      >
        <Form.Item name="file" label="File">
          <Upload
            onRemove={(e) => removeFile(true)}
            action="https://www.mocky.io/v2/5cc8019d300000980a055e76"
            listType="picture"
            onChange={(e) => {
              removeFile(false);
              chooseFile(e.file.originFileObj as File);
            }}
            maxCount={1}
          >
            <Button icon={<UploadOutlined />}>Upload File</Button>
          </Upload>
        </Form.Item>
        <Form.Item label="First Name">
          <Input
            value={state.firstName}
            onChange={(e) => dispatch(setFirstName(e.target.value))}
          />
        </Form.Item>
        <Form.Item label="Last Name">
          <Input
            value={state.lastName}
            onChange={(e) => dispatch(setLastName(e.target.value))}
          />
        </Form.Item>
        <Form.Item label="Phone Number">
          <Input
            value={state.phone}
            onChange={(e) => dispatch(setPhone(e.target.value))}
          />
        </Form.Item>
        <Form layout="inline" style={{ marginBottom: "10px" }}>
          <Form.Item label="City">
            <Input
              value={state.city}
              onChange={(e) => dispatch(setUserCity(e.target.value))}
            />
          </Form.Item>

          <Form.Item label="Address">
            <Input
              value={state.address}
              onChange={(e) => dispatch(setAddress(e.target.value))}
            />
          </Form.Item>
        </Form>
        <Form.Item>
          <Button htmlType="submit" type="primary">
            Change
          </Button>
        </Form.Item>
      </Form>
    </>
  );
};
export default Cabinet;
